import React from 'react'
const Nosotros = () =>{
    /*const datos = [
        {id: 1, nombre: 'ReactJs'},
        {id: 2, nombre: 'vuejs'},
        {id: 3, nombre: 'angular'}
    ]*/
    const [equipo, setEquipo] = React.useState([])

    React.useEffect(() =>{
//console.log('useEffect')
obtenerDatos()
    }, [])
    const obtenerDatos = async () =>{
        const data = await fetch('http://localhost:8055/api/Cuarto-Requerimiento')
        const users = await data.json()
        setEquipo(users)
    }
return (
    <div>
        <h1 class="text-center text-success">Requerimiento 4</h1>
        <table class="table  table-bordered stacktable table-sm">
    <thead>
      <tr  class="table-success">
        <th scope="col">Codigo Proyecto</th>
        <th scope="col">Calificacion</th>
        <th scope="col">Observacion</th>
        <th scope="col">Nombre Cliente</th>
        <th scope="col">Direcion</th>
      </tr>
    </thead>
    <tbody>
{
    equipo.map(item => (
      <tr>
        <th scope="row" key={item.id}>{item.proyecto_id}</th>
        <td>{item.calificacion}</td>
        <td>{item.observacion}</td>
        <td>{item.cliente.primerNombre}  {item.cliente.segundoNombre} {item.cliente.primerApellido} {item.cliente.segundoApellido}</td>
        <td>{item.cliente.direccion} {item.cliente.nombreCiudad} {item.cliente.nombreRegion}</td>
      </tr>
     
    
    ))
}
</tbody>
  </table>
        
    </div>
)

}


export default Nosotros
