import React , {useState,useEffect} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

import './App.css';


  const Region = () =>{
    const [regions, setRegions] = useState([])
    useEffect(() =>{
        axios.get("http://127.0.0.1:8000/api/welcome")
        .then(response => {
            setRegions(response.data)
        })
    })
    const [idProvincias, setIdProvincias] = useState(-1);
    const handlerCArgarProvincias = function (e){
      const opcion = e.target.value;
      console.log(opcion);
      setIdProvincias(opcion);
    }
  
  return (
   <div className="container col-lg-12">
       <div className="row">
     <div className="form-group">
     <select name="region" className="form-control" onClick={handlerCArgarProvincias} >
       <option value={0}></option>
{   
    regions.map(item =>(
      
        <option key={item.region_id} value={item.region_id}>{item.nombre_regions}</option>
        
    
    ))
    }</select>
<select name="provincia" id="selprovincias"className="form-control" >
{idProvincias > 0 &&
(
  regions[idProvincias].provincias.map((item, i) =>(
    <option key={"provincia"+ i} value=""> {item}</option>
  ))
)}
</select>

     </div>
     </div>
   </div>
  );

}


export default Region;
