import React , {Component} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

import './App.css';
const [provincia, setIdProvincia] = useState(-1);
const handlerCArgarRegion = function (e){
const opcion = e.target.value;
}
class Region extends Component {
  state ={ region:[]
}
componentDidMount(){
  axios
  .get("http://127.0.0.1:8000/api/welcome")
  .then((response) =>{
    console.log(response);
    this.setState({region: response.data})
})
.catch((error) =>{
  console.log(error);
});
}


  render(){  
  return (
   <div className="container col-lg-12">
       <div className="row">
     <div className="form-group">
<select name="region" className="form-control" onClick={handlerCArgarRegion}>
  {this.state.region.map(elemento=>(

    <option key={elemento.id} value={elemento.region_id}>{elemento.nombre_regions}</option>
  ))}
</select>
<select name="provincia" className="form-control">
  {this.state.region.map(elemento=>(

    <option key={elemento.id} value={elemento.id_provincia}>{elemento.nombreprovi}</option>
  ))}
</select>
     </div>
     </div>
   </div>
  );
}
}


export default Region;
